import React, { useState, useRef, useEffect } from "react";
import { Box, Typography, TextField, Button, Paper } from "@mui/material";

function App() {
  const [userInput, setUserInput] = useState("");
  const [chatLog, setChatLog] = useState([]); // Stores all chat messages
  const chatEndRef = useRef(null); // Ref for the bottom of the chat log

  // Scroll to the bottom of the chat window when chatLog changes
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [chatLog]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Add user input to chat log
    setChatLog([...chatLog, { role: "user", content: userInput }]);

    try {
      // Call the Azure Function API
      const response = await fetch('/api/query', {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ query: userInput }),
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }

      const data = await response.json();

      if (data.status === "success") {
        // Add assistant response to chat log
        setChatLog((prev) => [
          ...prev,
          { role: "assistant", content: "Here is the result:" },
          { role: "assistant", content: JSON.stringify(data.data) }, // Example: Add data as content
        ]);
      } else {
        setChatLog((prev) => [
          ...prev,
          { role: "assistant", content: data.message },
        ]);
      }
    } catch (error) {
      console.error("Error calling Azure Function:", error);
      setChatLog((prev) => [
        ...prev,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    }

    // Clear user input
    setUserInput("");
  };

  return (
    <Box
      sx={{
        maxWidth: "800px",
        margin: "0 auto",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        gap: "20px",
      }}
    >
      {/* Static Content Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h4" gutterBottom>
          ACS Inquery Tool
        </Typography>
        <Typography variant="body1">
          Use the chat window below to ask your questions. The chat log will
          display the conversation, and the results will be dynamically updated
          as part of the chat.
        </Typography>
      </Paper>

      {/* Chat Window Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h5" gutterBottom>
          Chat Window
        </Typography>
        <Box
          sx={{
            maxHeight: "300px",
            overflowY: "auto",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            marginBottom: "10px",
          }}
        >
          {chatLog.map((entry, index) => (
            <Typography
              key={index}
              variant="body2"
              sx={{
                textAlign: entry.role === "user" ? "right" : "left", // Align user messages to the right
                color: entry.role === "user" ? "blue" : "green",
                marginBottom: "5px",
                display: "block", // Ensures messages are block elements for alignment
              }}
            >
              <strong>{entry.role === "user" ? "You:" : "Assistant:"}</strong>{" "}
              {entry.content}
            </Typography>
          ))}
          {/* Add a hidden div to track the bottom of the chat */}
          <div ref={chatEndRef}></div>
        </Box>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            variant="outlined"
            label="Enter your question"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            sx={{ marginBottom: "10px" }}
          />
          <Button variant="contained" type="submit" fullWidth>
            Submit
          </Button>
        </form>
      </Paper>
    </Box>
  );
}

export default App;
