import React, { useState } from "react";
import { Box, Typography, TextField, Button, Paper } from "@mui/material";

function App() {
  const [userInput, setUserInput] = useState("");
  const [chatLog, setChatLog] = useState([]);
  const [results, setResults] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Add user input to chat log
    setChatLog([...chatLog, { role: "user", content: userInput }]);

    try {
      // Simulate calling your Azure Function (replace with real API endpoint)
      const response = await fetch("/api/your-azure-function", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ input: userInput }),
      });
      const data = await response.json();

      // Add assistant response to chat log and set results
      setChatLog((prev) => [
        ...prev,
        { role: "assistant", content: data.result },
      ]);
      setResults(data.result);
    } catch (error) {
      console.error("Error calling Azure Function:", error);
      setResults("An error occurred. Please try again.");
    }

    // Clear user input
    setUserInput("");
  };

  return (
    <Box
      sx={{
        maxWidth: "800px",
        margin: "0 auto",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        gap: "20px",
      }}
    >
      {/* Static Content Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h4" gutterBottom>
          Welcome to the Query App
        </Typography>
        <Typography variant="body1">
          Use the chat window below to ask your questions. The results will be
          displayed in the results section.
        </Typography>
      </Paper>

      {/* Chat Window Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h5" gutterBottom>
          Chat Window
        </Typography>
        <Box
          sx={{
            maxHeight: "300px",
            overflowY: "auto",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            marginBottom: "10px",
          }}
        >
          {chatLog.map((entry, index) => (
            <Typography
              key={index}
              variant="body2"
              sx={{
                color: entry.role === "user" ? "blue" : "green",
                marginBottom: "5px",
              }}
            >
              <strong>{entry.role === "user" ? "You:" : "Assistant:"}</strong>{" "}
              {entry.content}
            </Typography>
          ))}
        </Box>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            variant="outlined"
            label="Enter your question"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            sx={{ marginBottom: "10px" }}
          />
          <Button variant="contained" type="submit" fullWidth>
            Submit
          </Button>
        </form>
      </Paper>

      {/* Results Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h5" gutterBottom>
          Results
        </Typography>
        <Typography variant="body1">{results || "No results yet."}</Typography>
      </Paper>
    </Box>
  );
}

export default App;
