import React, { useState } from "react";
import { Box, Typography, TextField, Button, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from "@mui/material";

function App() {
  const [userInput, setUserInput] = useState("");
  const [chatLog, setChatLog] = useState([]); // Stores all chat messages
  const [results, setResults] = useState([]); // Stores results for display in tables

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Add user input to chat log
    setChatLog([...chatLog, { role: "user", content: userInput }]);

    try {
      // Call the Azure Function API
      const response = await fetch('/api/query', {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ query: userInput }),
      });

      console.log("API Response Status:", response.status);

      if (!response.ok) {
        const errorDetails = await response.text(); // Get error details
        console.error("Error Details:", errorDetails);
        throw new Error(`Error: ${response.statusText}`);
      }

      const data = await response.json();
      console.log("API Response Data:", data);

      if (data.status === "success" && Array.isArray(data.data)) {
        setChatLog((prev) => [
          ...prev,
          {
            role: "assistant",
            content: "The results have been added to the results section below.",
          },
        ]);
        setResults((prev) => [...prev, { query: userInput, tableData: data.data }]);
      } else {
        setChatLog((prev) => [
          ...prev,
          { role: "assistant", content: data.message },
        ]);
      }
    } catch (error) {
      console.error("Error calling Azure Function:", error);
      setChatLog((prev) => [
        ...prev,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    }

    setUserInput("");
  };


  return (
    <Box
      sx={{
        maxWidth: "800px",
        margin: "0 auto",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        gap: "20px",
      }}
    >
      {/* Static Content Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h4" gutterBottom>
          ACS Inquiry Tool
        </Typography>
        <Typography variant="body1">
          Use the chat window below to ask your questions. The chat log will display the conversation, and results will appear in the section below.
        </Typography>
      </Paper>

      {/* Chat Window Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h5" gutterBottom>
          Chat Window
        </Typography>
        <Box
          sx={{
            maxHeight: "300px",
            overflowY: "auto",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            marginBottom: "10px",
          }}
        >
          {chatLog.map((entry, index) => (
            <Typography
              key={index}
              variant="body2"
              sx={{
                textAlign: entry.role === "user" ? "right" : "left", // Align user messages to the right
                color: entry.role === "user" ? "blue" : "green",
                marginBottom: "5px",
                display: "block", // Ensures messages are block elements for alignment
              }}
            >
              <strong>{entry.role === "user" ? "You:" : "Assistant:"}</strong> {entry.content}
            </Typography>
          ))}
        </Box>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            variant="outlined"
            label="Enter your question"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            sx={{ marginBottom: "10px" }}
          />
          <Button variant="contained" type="submit" fullWidth>
            Submit
          </Button>
        </form>
      </Paper>

      {/* Results Section */}
      <Paper elevation={3} sx={{ padding: "20px" }}>
        <Typography variant="h5" gutterBottom>
          Results
        </Typography>
        {results.map((result, index) => (
          <Box key={index} sx={{ marginBottom: "20px" }}>
            <Typography variant="h6">Query: {result.query}</Typography>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    {Object.keys(result.tableData[0]).map((key) => (
                      <TableCell key={key}>{key}</TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {result.tableData.map((row, rowIndex) => (
                    <TableRow key={rowIndex}>
                      {Object.values(row).map((value, colIndex) => (
                        <TableCell key={colIndex}>{value}</TableCell>
                      ))}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
        ))}
        {results.length === 0 && (
          <Typography variant="body2" color="textSecondary">
            No results to display. Submit a query to see results here.
          </Typography>
        )}
      </Paper>
    </Box>
  );
}

export default App;
